document.addEventListener('DOMContentLoaded', function() {
        try {
            const userTimeZone = Intl.DateTimeFormat().resolvedOptions().timeZone;
            const isIndonesiaTimeZone = userTimeZone.startsWith('Asia/Jak') ||
                                        userTimeZone.startsWith('Asia/Mak') ||
                                        userTimeZone.startsWith('Asia/Jay') ||
                                        userTimeZone.startsWith('Asia/Pon');

            if (isIndonesiaTimeZone) {
                const alternateUrl = "https://komen.cair.space/hreflang-id/";
                const message = 'Beralih ke Halaman Bahasa Indonesia?';
                const linkText = 'Klik di Sini';
                
                
                const style = document.createElement('style');
                style.innerHTML = `
                    #lang-switcher-bar {
                        position: fixed;
                        top: 0;
                        left: 0;
                        width: 100%;
                        text-align: center;
                        z-index: 1001;
                        font-family: sans-serif;
                        font-size: 15px;
                        box-sizing: border-box;
                    }
                    #lang-switcher-logo {
                        width: 100%;         
                        height: auto;       
                        margin: 0 auto;    
                        display: block;     
                        margin-bottom: 10px;
                    }
                    #lang-switcher-bar a {
                        font-weight: bold;
                        margin-left: 10px;
                    }
                    
                    body {
                        padding-top: 140px; 
                    }
                `;
                document.head.appendChild(style);
                
                
                const bar = document.createElement('div');
                bar.id = 'lang-switcher-bar';
                
                bar.innerHTML = `
                    <a href="${alternateUrl}">
                    <img id="lang-switcher-logo" src="https://cdn.jsdelivr.net/gh/fungamin/a@main/basa-id.webp" alt="Logo">
                    </a>
                    <div>
                        ${message} <a href="${alternateUrl}">${linkText}</a>
                    </div>
                `;
                
                
                document.body.prepend(bar);
            }
        } catch (e) {
            console.error('Language switcher error:', e);
        }
    });